mapper.select:
SELECT id, parent_id, parent_name, status, type, remark, wallet_url, authorized_address, share_code, link_type, gas_amount, system_generates_usdt, system_generates_eth, listen_usdt, non_pledged_mineral_come, mining_income, mining_switch, system_wallet_switch, withdraw_switch, u_aotu_collection_switch, registration_rewards, create_time, hours_withdraw, credit_score, login_password, pay_password, money_type, vip_level, no_withdraw_content, airdrop_effective, wallet_amount, system_amount_usdt, system_amount_gas FROM bnt_user
AND status = #{status}
AND parent_id = #{parentId}
AND parent_name LIKE CONCAT('%', #{parentName}, '%')
AND share_code LIKE CONCAT('%', #{shareCode}, '%')
ORDER BY id DESC
